import React from 'react';

import MainContainer from '../components/main-container';

import '../App.css';

var buttonPressed = "1"

export default class MessageContainer extends React.Component {
  constructor(props) {
    super(props);

    this.state = ({
      selectedObjects: [],
      total:0,
      page:"Ordenar",
      currentState:buttons[0].state0
    });
  }

  manageButtonsState = () => {
    var i  = 0;
    this.props.data.map((d) => {
      if(i===0){
        buttonPressed = d
      }
      i+=1
      return(<li>{d}</li>)
    })
    i=0
    console.log(buttonPressed)
    console.log(this.state.page)
    document.getElementsByClassName("showMoreNew")[0].click();

  }

  render(){
    return (
      <div className="AppFS">
        {this.manageButtonsState()}
        <div className="buttonDiv">
            <button className="backButton" style = {{"visibility":this.state.buttonState[0].visibility}}>{props.text}</button>
            <Button text={this.state.buttonState[0].label} css="backButton" visibility={this.state.buttonState[0].visibility}/>
            <Button text={this.state.buttonState[1].label} css="button" visibility={this.state.buttonState[1].visibility}/>
            <Button text={this.state.buttonState[2].label} css="button" visibility={this.state.buttonState[2].visibility}/>
            <Button text={this.state.buttonState[3].label} css="button" visibility={this.state.buttonState[3].visibility}/>
            <Button text={this.state.buttonState[4].label} css="button" visibility={this.state.buttonState[4].visibility}/>
        </div>
      </div>
    )
  }
}


const buttons = [
                [{label:"Back", visibility:"hidden"},
                 {label:"Ordenar", visibility:"visibile"},
                 {label:"Llamar mesero", visibility:"visibile"},
                 {label:"Pagar", visibility:"visibile"},
                 {label:"Back", visibility:"hidden"}],
                [{label:"Back", visibility:"visibile"},
                 {label:"Ordenar", visibility:"hidden"},
                 {label:"Llamar mesero", visibility:"visibile"},
                 {label:"Pagar", visibility:"visibile"},
                 {label:"Seguir", visibility:"visibile"}]
                ]
