import React from 'react';

import MainContainer from '../components/main-container';

import '../App.css';

var buttonPressed = "1"

export default class MessageContainer extends React.Component {
  constructor(props) {
    super(props);

    this.state = ({
      selectedObjects: [],
      button: [
        {label:"Back", visibility:true},
        {label:"Ordenar", visibility:true},
        {label:"Llamar mesero", visibility:true},
        {label:"Pagar", visibility:true},
        {label:"Back", visibility:true}
      ],
      total:0,
      page:"Ordenar",
      currentState:buttons[0].state0
    });
  }

  manageButtonsState = () => {
    var i  = 0;
    this.props.data.map((d) => {
      if(i===0){
        buttonPressed = d
      }
      i+=1
      return(<li>{d}</li>)
    })
    i=0
    console.log(buttonPressed)
    console.log(this.state.page)

  }

  render(){
    return (
      <div className="AppFS">
        {this.manageButtonsState()}
        <div className="buttonDiv">
            <button className="backButton" style = {{"visibility":this.state.button[0].visibility}}>{this.state.button[0].label}</button>
            <button className="button" style = {{"visibility":this.state.button[1].visibility}}>{this.state.button[1].label}</button>
            <button className="button" style = {{"visibility":this.state.button[2].visibility}}>{this.state.button[2].label}</button>
            <button className="button" style = {{"visibility":this.state.button[3].visibility}}>{this.state.button[3].label}</button>
            <button className="button" style = {{"visibility":this.state.button[4].visibility}}>{this.state.button[4].label}</button>
        </div>
      </div>
    )
  }
}


const buttons = [
                [{label:"Back", visibility:"hidden"},
