import React from 'react'
import '../../App.css';
import MenuItem from '../../components/menu-item'

var itemsTest = [
    {name:"arroz",value:"0",text:"Arroz"},
    {name:"frijoles",value:"1",text:"Frijoles"},
    {name:"huevo",value:"2",text:"Huevo"},
    {name:"manzana",value:"3",text:"Manzana"},
    {name:"pera",value:"4",text:"Pera"},
    {name:"tamales",value:"5",text:"Tamales"},
    {name:"panqueques",value:"6",text:"Panqueques"},
    {name:"maduro",value:"7",text:"Maduro"},
    {name:"chorizo",value:"8",text:"Chorizo"},
    {name:"natilla",value:"9",text:"Natilla"},
    {name:"pinto",value:"10",text:"Pinto"},
    {name:"tostada",value:"11",text:"Tostada"}
]

const menuContainer = (props) => {
    var text = props.page
    if(text==="Inicio"){
        return (
            <div className="inicio">
                <p>
                    Bienvenido al restaurante!
                </p>
            </div>
        )
    }
    else{
        const listItems = Object.keys(itemsTest).map((item, i) =>
            <MenuItem name={itemsTest[item].name} value={itemsTest[item].value} text={itemsTest[item].text}/>

        );
        return (
            <div className="menu-box">
                {listItems}
            </div>
        )
    }
};

export default menuContainer;
