import React, { Component } from 'react';
import './App.css';
import { Connector } from 'mqtt-react';
import { subscribe } from 'mqtt-react';
import Fullscreen from "react-full-screen";

import ButtonP from './components/buttons'

const _MessageList = (props) => {
  return <p>Button Clicked: {props.data}</p>
};

// simple subscription to messages
const MessageList = subscribe({topic: 'restaurant/buttons'})(_MessageList)

class App extends Component {
  constructor(props) {
    super();

    this.state = {
      isFull: false,
      button1: "Back",
      button2: "Back",
      button3: "Back",
      button4: "Back",
      button5: "Back"
    };
  }

  goFull = () => {
    this.setState({ isFull: true });
  }


  render() {
    return (
      <div className="App">
        <button onClick={this.goFull}>
          Go Fullscreen
        </button>

        <Fullscreen
          enabled={this.state.isFull}
          onChange={isFull => this.setState({isFull})}
        >
          <div className="fullscreen-enabled">
            <Connector mqttProps="ws://192.168.100.103:12345/">
              <div >
                <ButtonP text={this.state.button1}/>
              </div>
              <div className="AppFS">
                <MessageList/>
              </div>
            </Connector>
          </div>
        </Fullscreen>

      </div>
    );
  }
}

export default App;
