import React from 'react';

import Header from '../components/header';
import MenuContainer from '../components/menuContainer';

import '../App.css';

var buttonPressed = "1"

export default class MessageContainer extends React.Component {
  constructor(props) {
    super(props);

    this.state = ({
      selectedObjects: [],
      button: [
        {label:"Back", visibility:true},
        {label:"Ordenar", visibility:true},
        {label:"Llamar mesero", visibility:true},
        {label:"Pagar", visibility:true},
        {label:"Back", visibility:true}
      ],
      total:0,
      //There are 
      page:"Inicio",
      currentButtonsState:buttons[0]
    });
  }

  manageButtonsState = () => {
    buttonPressed=this.props.data.slice(0,1)[0]
    //console.log(buttonPressed)
    if(buttonPressed==="button1"||buttonPressed==="button2"||buttonPressed==="button3"||buttonPressed==="button4"||buttonPressed==="button5"){
      document.getElementById(buttonPressed).click()
    }
    buttonPressed = "1"
  }

  stateManager = (button) => {
    console.log(button)
    switch (buttonPressed) {
      case 1:
        //Retornar button is only available in Categorias, Menu and Pagar
        switch (this.state.page){
          //Categorias
          case "Categorias": 
            console.log("Going from Categorias to Inicio") 
            this.setState({currentState:buttons[0]})
            break;
          //Menu
          case 'Breakfast': 
            console.log("Going from Menu to Categorias")
            this.setState({currentState:buttons[1]})
            break;
          case 'Lunch': 
            console.log("Going from Menu to Categorias")
            this.setState({currentState:buttons[1]})
            break;
          case 'Dinner':
            console.log("Going from Menu to Categorias")
            this.setState({currentState:buttons[1]}) 
            break;
          case 'Drinks': 
            console.log("Going from Menu to Categorias")
            this.setState({currentState:buttons[1]})
            break;
          //Pagar
          case 'Pagar': 
            console.log("Going from Pagar to Inicio")
            this.setState({currentState:buttons[0]})
            break;
          default:
            break;
        }
      case 2:
        //Ordenar button is only available in Inicio
        switch (this.state.page){
          case "Inicio": 
            console.log("Going from Inicio to Categorias") 
            this.setState({currentState:buttons[1]})
            break;
          default:
            break;
        }
      case 3:
        //Llamar meser button is always available

        //do something

        break;
      case 4:
        //Pagar o deseleccionar todo button is only available in Inicio and Menu 
        switch (this.state.page){
          //Categorias
          case "Inicio": 
            console.log("Going from Inicio to Pagar") 
            this.setState({currentState:buttons[3]})
            break;
          //Menu
          case 'Breakfast': 
            console.log("Deselected in Breakfast")
            break;
          case 'Lunch': 
            console.log("Deselected Lunch")
            break;
          case 'Dinner':
            console.log("Deselected Dinner")
            break;
          case 'Drinks': 
            console.log("Deselected Drinks")
            break;
          default:
            break;
        }
      case 5:
        //Ordenar button is only available in Categorias
        switch (this.state.page){
          //Categorias
          case "Categorias": 
            console.log("Going from Categorias to Ordenar") 
            this.setState({currentState:buttons[3]})
            break;
          default:
            break;
        }
    }

  }

  render(){
    return (
      <div className="AppFS">
        {this.manageButtonsState()}
        <div className="buttonDiv">
            <button onClick={this.stateManager.bind(this,1)} id="button1" className="backButton" style = {{"visibility":this.state.button[0].visibility}}>{this.state.button[0].label}</button>
            <button onClick={this.stateManager.bind(this,2)} id="button2" className="button" style = {{"visibility":this.state.button[1].visibility}}>{this.state.button[1].label}</button>
            <button onClick={this.stateManager.bind(this,3)} id="button3" className="button" style = {{"visibility":this.state.button[2].visibility}}>{this.state.button[2].label}</button>
            <button onClick={this.stateManager.bind(this,4)} id="button4" className="button" style = {{"visibility":this.state.button[3].visibility}}>{this.state.button[3].label}</button>
            <button onClick={this.stateManager.bind(this,5)} id="button5" className="button" style = {{"visibility":this.state.button[4].visibility}}>{this.state.button[4].label}</button>
        </div>
        <div className="menuDiv">
            <Header text={this.state.page}/>
            <MenuContainer page={this.state.page} css="menu-box"/>
        </div>
      </div>
    )
  }
}


